package BaekJoon.InOutput;

import java.util.Scanner;

public class B10950 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner sc=new Scanner(System.in);
		int n=sc.nextInt();

		for(int i=0;i<n;i++) {
			int A=sc.nextInt();
			int B=sc.nextInt();
			System.out.println(A+B);
		}

	}

}
